INSERT INTO themes (name) VALUES 
('общая');
INSERT INTO age_groups (name, age_from, age_to) VALUES 
('4-5', 4, 5),
('6-7', 6, 7);
INSERT INTO questions (text, theme_id, age_group_id, right_answer, fact, hint) VALUES 
('Сколько тебе лет и сколько будет через год?', 1, 1, '', 'Правильно! Каждый год в день рождения мы становимся на один год старше, мудрее и сильнее!', 'Давай посчитаем! Если сейчас тебе, например, четыре года, то через год будет... пять! Попробуй посчитать сам.'),
('Какого цвета солнышко?', 1, 1, 'Желтого, оранжевого', 'В космосе солнце кажется белым', 'На что похож цвет солнца?'),
('Кто живет в лесу?', 1, 1, 'Медведь, заяц, лиса, волк и т.д.', 'Зайцы зимой меняют шубку на белую, чтобы быть незаметными на снегу', 'Вспомни зверей из книжек или мультфильмов?'),
('Для чего нужен зонтик?', 1, 1, 'Чтобы не промокнуть под дождем', 'Первые зонтики появились в Китае', 'Что мы берем с собой, когда на улице дождик?'),
('Что нужно делать перед сном?', 1, 1, 'Чистить зубки, умываться, надевать пижаму', 'Во сне детки растут и набираются сил', 'Что мы делаем вечером, чтобы подготовиться ко сну?'),
('Что мы видим на небе ночью?', 1, 1, 'Луну и звезды', 'Луна не светит сама, а только отражает солнечные лучи.', 'Что светится в темном небе?'),
('Какие бывают времена года?', 1, 1, 'Зима, весна, лето, осень', 'Смена времён года заметна не в каждом уголке Земли. Например, в Антарктиде и на Северном полюсе всегда лежит снег, так как тепло туда плохо доходит из-за наклона земной оси', 'Их всего четыре. Посмотри в окно и скажи какое сейчас  время года . А какое будет после?'),
('Какое сейчас время года?', 1, 1, 'Лето / Зима / Весна / Осень', 'Каждая пора года имеет свои признаки.', 'Посмотри в окно: тепло или холодно? Есть ли снег или зелёная трава?'),
('Какая бывает погода?', 1, 1, 'Погода быввает солнечная, дождливая, снежная, ветреная', 'Прогноз погоды помогает одеваться правильно', 'Посмотри в окно - солнечно или облачно?'),
('Кто живёт в улье?', 1, 1, 'Пчёлы', 'Пчёлы не могут жить без воды — одной пчелиной семье нужно в день около двух стаканов воды', 'Маленькие насекомые, делают мёд.'),
('Назови какие предметы бывают круглыми', 1, 1, 'колесо, солнце, мяч, тарелка, бублик', 'Круглые предметы могут катиться, а не просто лежать на месмте', 'Посмотри вокруг, видишь ли ты что-то круглое? Вот мяч, например круглый? А что на него ещё похоже?'),
('Где живут рыбы?', 1, 1, 'В реках, озёрах, морях и океанах', 'Некоторые рыбы, например, летучие рыбы, могут выпрыгивать из воды и парить над ней несколько секунд!', 'Где можно увидеть рыб?'),
('Когда земля покрыта снегом?', 1, 1, 'Зимой, когда на улице очень холодно', 'Снежинки всегда имеют шесть лучиков, но ни одна не повторяет другую по форме', 'В какое время года идет снег?'),
('Какой твой любимый фрукт?', 1, 1, '', 'Здоровый выбор! Фрукты — это вкусные витаминки, которые помогают нам расти сильными и здоровыми.', 'Что ты любишь кушать из сладких фруктов: яблочко, бананчик или, может, апельсинчик?'),
('Кем ты будешь, когда вырастешь?', 1, 1, '', 'Какая замечательная мечта! Чтобы ей стать, нужно хорошо кушать, много играть и, скоро, пойти в школу, где ты всему научишься!', 'Представь, кем ты хочешь работать: врачом,  строителем, учителем или кем-то еще?'),
('Что ты любишь делать больше всего на свете?', 1, 1, '', 'Это очень здорово! У каждого человека есть свое любимое дело, которое приносит ему радость', 'Что тебе нравится: рисовать, играть в машинки, танцевать, гулять?'),
('Каких домашних животных ты знаешь?', 1, 1, 'Собака, кошка, хомячок', 'Собаки понимают до 250 слов, а кошки мяукают только для людей', 'Какие животные живут с людьми в доме?'),
('В какой стране мы живем?', 1, 1, 'Россия', 'Россия — самая большая страна в мире! Здесь есть и снежные зимы, и жаркое лето, и красивые леса, и высокие горы', 'Это большая страна, а столица нашей страны — Москва'),
('Как назвать одним словом груша, яблоко, апельсин, киви?', 1, 1, 'Фрукты', 'Фрукты — это очень вкусные и полезные продукты. В них много витаминов, которые помогают нам быть здоровыми и сильными!', 'Все они растут на деревьях или кустах, они сладкие или кисло-сладкие, и их можно кушать. Что это?'),
('Как называется детеныш коровы?', 1, 1, 'Теленок', 'Мама-корова очень заботливая! Она узнает своего теленка по запаху и всегда находит его в стаде', 'он говорит «му-у-у», как его мама, но голосок у него совсем маленький и тоненький'),
('Как называется детеныш лошади?', 1, 1, 'Жеребёнок или жеребенок, любой из этих ответов', 'Жеребята умеют стоять на ножках и даже бегать уже через несколько часов после своего рождения!', 'Он очень любит бегать и резвиться рядом с мамой-лошадкой'),
('Зачем автомобилю тормоза?', 1, 1, 'Чтобы вовремя остановиться и не столкнуться с другими машинами или людьми', 'Тормоза — это одна из самых важных частей автомобиля! Представь, что ты бежишь, а потом нужно быстро остановиться. Тормоза помогают сделать то же самое машине.', 'Что помогает машине остановиться у светофора или перед пешеходным переходом, чтобы пропустить людей?'),
('Какие ты знаешь виды транспорта?', 1, 1, 'Машина, автобус, поезд, самолёт, корабль, велосипед.', 'Здорово! Транспорт бывает очень разным: например, снегоход едет по снегу, а вертолёт может лететь куда угодно!', 'Вспомни, на чём можно ехать по дороге, плыть по воде или лететь по небу?'),
('Для чего человеку нужны зубы?', 1, 1, 'Чтобы кусать и жевать еду', 'А еще зубы помогают нам красиво улыбаться и четко говорить! Поэтому их нужно беречь и чистить два раза в день.', 'Вспомни, как ты кушаешь'),
('Из чего можно построить дом?', 1, 1, 'Из кирпичей, из дерева (брёвна, доски), из бетонных блоков', 'Дома бывают очень разными! В жарких странах их иногда строят из глины, чтобы внутри было прохладно, а в холодных — из толстых брёвен, чтобы сохранять тепло', 'Вспомни, из каких материалов взрослые строят стены домов на улице? (Кирпичи, деревянные брусья, бетонные блоки)'),
('Назови части тела человека.', 1, 1, 'Голова, руки, ноги, туловище, глаза, нос, рот, уши', 'Тело человека на 60% состоит из воды. Вода поддерживает работу органов, клеток и крови.', 'Покажи на себе: что помогает нам ходить, брать предметы, видеть и улыбаться?'),
('Назови 5 молочных продуктов', 1, 1, 'Молоко, кефир, сыр, творог, йогурт, сметана.', 'Молоко дает нам сила и крепкие косточки! А еще из молока делают мороженое — это тоже молочный продукт, только очень вкусный и сладкий!', 'Вспомни, что можно купить в молочном отделе магазина? Что делают из молока?'),
('Как называется врач, который лечит животных?', 1, 1, 'Ветеринар', 'Ветеринар — очень важная профессия! Он помогает нашим пушистым друзьям быть здоровыми и счастливыми', 'Этот доктор лечит кошечек, собачек, хомячков и даже слонов!'),
('Какие зимние виды спорта ты знаешь?', 1, 1, 'Катание на санках, лыжах, коньках, лепка снеговика.', 'Зимние виды спорта не только веселые, но и полезные — они укрепляют наши ножки и ручки!', 'В какие игры можно играть, когда на улице снег и лёд?'),
('Какие летние виды спорта ты знаешь?', 1, 1, 'Плавание, футбол, катание на велосипеде, бег.', 'Летом можно заниматься спортом на свежем воздухе — это очень полезно для здоровья!', 'Чем можно заниматься на улице, когда тепло и светит солнышко?'),
('Кем ты являешься для своих дяди и тети?', 1, 1, 'Племянник или племянница', 'Племянники и племянницы часто бывают очень любимыми детьми для своих дядь и тётей! Они могут играть вместе, ходить в гости и весело проводить время.', 'Дядя и тётя — это брат и сестра твоего папы или мамы. А ты для них...?'),
('Чем покрыто тело рыбы?', 1, 1, 'Чешуёй', 'Чешуя защищает рыбу как броня — от ран и болезней!', 'Это скользкое и блестящее покрытие помогает рыбам плавать'),
('Какое общее название имеют ножи, вилки и ложки?', 1, 1, 'Столовые приборы.', 'Столовые приборы — наши верные помощники за обедом! Интересно, что самой «древней» из них является ложка — люди придумали её очень давно!', 'Эти предметы лежат на кухне и помогают нам кушать.'),
('Как называется врач, который лечит зубы?', 1, 1, 'Стоматолог или зубной врач.', 'Этот врач не только лечит больные зубки, но и помогает им быть крепкими и здоровыми! Он может поставить  пломбу', 'Когда у тебя болит зуб, кто его осматривает и лечит с помощью маленького зеркальца и сверла?'),
('Когда можно кататься на санках – зимой или летом?', 1, 1, 'зимой', 'Cанки — это отличный способ весело провести время на свежем воздухе зимой! А ещё катание на санках помогает укрепить мышцы ног и развивает координацию.', 'для этого нужно много снега и холодная погода'),
('Какое животное больше – лошадь или собака?', 1, 1, 'Лошадь', 'Лошадь такая большая и сильная, что может катать на своей спине не только детей, но и взрослых! А ещё она может перевозить очень тяжёлые грузы.', 'Посмотри, кого можно оседлать и кататься верхом, а кого мы держим на поводке?'),
('Что наступает после ночи?', 1, 1, 'Утро', 'Утро — это начало нового дня! В это время птицы поют свои первые песни, а солнце начинает освещать землю своими лучиками.', 'Когда темнота проходит, на небе появляется солнышко, и все просыпаются. Что это бывает?'),
('Каких птиц ты знаешь?', 1, 1, 'Воробей, голубь, синица, ворона, сорока.', 'А ты знал, что синички любят сало? А воробушки очень дружные — они целыми стайками перелетают с места на место. ', 'Вспомни, каких пернатых друзей ты часто видишь на улице? Кто клюёт крошки у тебя за окном или весело чирикает на деревьях?'),
('Каких животных ты знаешь?', 1, 1, 'Медведь, заяц, лиса, кошка, собака, корова, лошадь.', 'Если кот обвивает хвостом ноги человека, значит, он хочет быть его лучшим другом. В такой момент кот выпрашивает либо внимание, либо мисочку корма', 'Вспомни, кто живёт в лесу, кого можно встретить в деревне у бабушки или кого мы видим в зоопарке?'),
('Самое быстролетающее насекомое – это..', 1, 1, 'Стрекоза', 'Стрекозы — настоящие чемпионы по полёту! Они могут зависать в воздухе на одном месте, как вертолёты, и развивать огромную скорость, чтобы поймать другую букашку.', 'Это насекомое может летать не только вперёд, но и назад, и даже в стороны! Её крылышки мелькают так быстро, что их почти не видно.'),
('Как ухаживают за цветами?', 1, 1, 'Их нужно поливать, ставить на солнышко и иногда протирать листочки.', 'Цветы, как и люди, любят, когда с ними разговаривают и гладят их листочки! Говорят, от ласковых слов они растут еще лучше и пышнее.', 'Чтобы цветочек рос красивым и здоровым, ему нужны три главные вещи: водичка, свет и  забота'),
('Кто длиннее: червячок или змея?', 1, 1, 'Змея', 'Самые длинные змеи в мире — сетчатые питоны — могут достигать длины более 10 метров! А самый длинный дождевой червь, обитающий в Австралии, вырастает до 3 метров. Но обычный дождевой червь, которого мы видим, редко бывает длиннее 15-20 сантиметров.', 'Посмотри, кого можно легко удержать в ладошке, а кто может быть таким же длинным, как палка или даже веревка?'),
('Что больше: яблоко или арбуз?', 1, 1, 'Арбуз', 'Арбуз на 91% состоит из воды. Он вырастает в среднем за 90 дней', 'Подумай, что ты можешь съесть за один раз целиком, а что нужно резать на много кусочков для всей семьи?'),
('Что можно смотреть с закрытыми глазами', 1, 1, 'Сны, мечты', 'Иногда во сне можно летать, разговаривать с животными или попадать в сказочные страны.', 'Это не картинка и не мультик. Это бывает, когда ты крепко спишь или просто мечтаешь о чём-то очень приятном.'),
('Сколько человек в вашей семье?', 1, 1, '', 'В России есть праздник — День семьи, любви и верности, символом которого считается ромашка', 'Давай посчитаем, кто живёт с тобой в одном доме');
INSERT INTO questions (text, theme_id, age_group_id, right_answer, fact, hint) VALUES 
('Сколько тебе лет и сколько будет через год?', 1, 2, '', '', ''),
('Почему птицы улетают на юг зимой?', 1, 2, 'Чтобы найти еду и тепло в зимний период', 'Миграция — это сезонное перемещение птиц из одного региона в другой. Для многих видов миграция происходит два раза в год: они улетают осенью и снова возвращаются весной.', 'на юге зимой тепло?'),
('Где живут рыбы и почему они не могут жить на суше?', 1, 2, 'Рыбы живут в воде и дышат кислородом через жабры, извлекая его из воды. На суше их жабры слипаются, и они не могут дышать.', 'Существует рыба-ползун, которая может выходить на сушу и ползать по берегу с помощью плавников, а некоторые двоякодышащие рыбы имеют и жабры, и лёгкие!', 'Чем дыхание рыб отличается от нашего?'),
('Почему зимой земля покрывается снегом, а летом нет?', 1, 2, 'Зимой в облаках очень холодно. Капельки воды замерзают и превращаются в кристаллики льда — снежинки. Летом в облаках теплее, и капельки воды остаются жидкими, выпадая дождём.', 'Снег на самом деле не белого цвета, а полупрозрачный. Белыми снежинки кажутся из-за отражающегося от них солнечного света', 'Что происходит с водой в облаках при разной температуре?'),
('Какой твой любимый фрукт и почему?', 1, 2, '', 'Отличный выбор! Интересно, что цвет фрукта часто говорит о его пользе. Например, оранжевые фрукты полезны для глаз.', 'Чем тебе нравится именно этот фрукт: вкусом, сочностью, цветом или, может, он напоминает о лете?'),
('Почему зимой идет снег, а летом - дождь?', 1, 2, 'Зимой холодно, и капельки воды замерзают, превращаясь в снежинки', 'Каждая снежинка имеет уникальную форму, и нет двух одинаковых', 'Что происходит с водой при разных температурах?'),
('В какой стране мы живем? Назови её столицу', 1, 2, 'Российская Федерация или Россия. Столица - Москва', 'Россия - самое большое государство в мире, её территория охватывает 11 часовых поясов! А Москва - один из самых красивых и больших городов Европы', 'Вспомни,  какой город является главным в нашей стране'),
('Для чего человеку нужны зубы?', 1, 2, 'чтобы откусывать и пережевывать еду', 'Интересно, что у человека за жизнь вырастает 20 молочных зубов, которые затем сменяются 32 постоянными. А самая твердая ткань в нашем организме — это зубная эмаль!', 'Подумай, как мы едим: откусываем хлеб, пережевываем мясо'),
('Как называется главный город страны?', 1, 2, 'Москва', 'столица России — Москва — один из самых больших городов Европы!', 'В этом городе работает правительство, и он обычно самый большой и известный'),
('Из чего строят до', 1, 2, 'кирпич, дерево, бетон, камень', 'Раньше на Руси дома строили в основном из дерева, а в Древнем Риме — из камня и бетона. Сегодня используют каркасные технологии, позволяющие построить дом за несколько недель!', 'Подумай о свойствах материалов: прочность, сохранение тепла, долговечность'),
('Как называется врач, который лечит животных?', 1, 2, 'Ветеринар (ветеринарный врач).', 'Ветеринары лечат не только домашних питомцев, но и сельскохозяйственных животных, а иногда даже обитателей зоопарков!', 'Эта профессия требует знаний в зоологии и медицины.'),
('Какие зимние виды спорта ты знаешь?', 1, 2, 'Лыжные гонки, биатлон, фигурное катание, хоккей, сноуборд, кёрлинг.', 'Биатлон сочетает лыжную гонку и стрельбу — это требует огромной концентрации!', 'Вспомни спортивные дисциплины зимних Олимпийских игр.'),
('Какие летние виды спорта ты знаешь?', 1, 2, 'Лёгкая атлетика, плавание, футбол, гимнастика, велоспорт, теннис.', 'Современные Олимпийские игры включают более 40 летних видов спорта!', 'Какие соревнования проходят на летних Олимпийских играх?'),
('Кем ты приходишься своей бабушке?', 1, 2, 'Внук или внучка', 'Слово "внук" произошло от древнего значения "следующее поколение"!', 'Бабушка это мама одного из родителей'),
('Кто такие хищники?', 1, 2, 'Животные, преимущественно питающиеся другими животными. Имеют острые клыки и когти.', 'Некоторые хищники всеядны, как медведь, а другие — строгие плотоядные, как тигр!', 'Как ты думаешь чем они питаются?'),
('Какой день недели находится между четвергом и субботой?', 1, 2, 'Пятница.', 'Название "пятница" происходит от числа "пять" — пятый день недели!', 'Вспомни последовательность дней недели.'),
('Чем покрыто тело рыбы?', 1, 2, 'Чешуёй', 'Чешуя растёт по принципу: под старой верхней чешуёй образуется нижняя молодая.', 'Какой покров обеспечивает защиту и уменьшает сопротивление воды?'),
('Сколько букв в русском алфавите?', 1, 2, '33 буквы: 10 гласных, 21 согласная, Ь и Ъ (не обозначают звуков). Согласные делятся на звонкие/глухие, твёрдые/мягкие.', 'Молодец! В нашем алфавите есть очень интересные буквы: например, буква «Й» всегда дружит с гласными, а «Ь» и «Ъ» не имеют собственных звуков, но помогают другим буквам!', 'Сможешь повторить алфавит?'),
('Назови четвертый месяц года?', 1, 2, 'Апрель.', 'Апрель — месяц, когда природа просыпается! В этом месяце часто идёт дождик, который помогает расти первой травке и цветам.', 'Этот месяц приходит после марта и перед маем.'),
('Почему человек не может использовать морскую воду для питья?', 1, 2, 'Высокая концентрация соли нарушает водно-солевой баланс: почки не могут вывести избыток соли, и организм теряет воду.', 'Учёные разрабатывают методы опреснения морской воды. Например, в ОАЭ таким способом получают 90% питьевой воды!', 'Может ты знаешь какая на вкус морская вода?'),
('Какое общее название имеют ножи, вилки и ложки?', 1, 2, 'Столовые предметы', 'В разных культурах существуют особые приборы: в Азии — палочки, в Средней Азии — пиала (чаша).', 'Подумай, для чего используется каждый из этих предметов.'),
('Как называется врач, который лечит зубы?', 1, 2, 'Стоматолог (или дантист). Основные направления: терапия (лечение кариеса), ортодонтия (исправление прикуса), хирургия (удаление зубов).', 'А знаешь ли ты, что первая зубная щётка с щетиной была изобретена в Китае ещё в 15 веке!', 'Подумай, чем может заниматься этот врач, кроме лечения кариеса.'),
('Почему происходит смена дня и ночи?', 1, 2, 'Смена дня и ночи происходит потому, что наша планета Земля постоянно вращается вокруг своей оси. Та сторона, которая повёрнута к Солнцу, освещена — там день, а противоположная — в тени, там ночь.', 'Полный оборот Земля совершает за 24 часа, которые называются сутками. Интересно, что на Северном полюсе летом можно наблюдать полярный день, когда солнце не заходит за горизонт несколько недель', 'Подумай, какое появляется на небе ночью?'),
('Что бывает раньше – обед или ужин?', 1, 2, 'Обед', 'Интересно, что слово "ужин" произошло от древнего слова "угъ", что означало "юг". А почему? Потому что ужинали, когда солнце было на юге (то есть в полдень)! Со временем главный приём пищи сместился на вечер, а название осталось.', 'Подумай, в какой последовательности идут приемы пищи в течение дня.'),
('Каких птиц ты знаешь?', 1, 2, 'Воробей, синица (насекомоядная, зимой прилетает к кормушкам), голубь (городской, питается семенами и остатками еды), снегирь (зимний гость с красной грудкой), ласточка (насекомоядная, улетает на юг).', 'У каждой птицы свои удивительные способности! Ласточки почти всю жизнь проводят в воздухе, ловя насекомых. А дятла называют "санитаром леса" потому, что он клювом достаёт из-под коры вредных жуков-короедов.', 'Подумай, какие птицы остаются зимовать, а какие улетают в тёплые края?'),
('Каких животных ты знаешь и на какие группы их можно разделить?', 1, 2, 'Медведь, волк, лиса (лесные хищники), заяц, белка (лесные травоядные), корова, лошадь (домашние травоядные), кошка, собака (домашние животные).', 'Учёные делят животных на классы: млекопитающие, беспозвоночные, рыбы, насекомые, земноводные и пресмыкающие. Самые умные животные — это приматы (например, обезьяны) и китообразные (дельфины). Они даже могут узнавать себя в зеркале!', 'Подумай, где живут эти животные (лес, ферма, дом) и чем они питаются (хищники, травоядные).'),
('Какое насекомое летает быстрее всех?', 1, 2, 'Стрекоза. Некоторые её виды, могут развивать скорость до 55-60 км/ч.', 'Стрекозы — одни из древнейших насекомых на Земле! Они появились ещё до динозавров. А их огромные глаза состоят из 30 000 маленьких глазков-фасеток, что позволяет им видеть почти на 360 градусов и быть отличными охотниками.', 'Это насекомое может летать не только вперёд, но и назад, и даже в стороны! Её крылышки мелькают так быстро, что их почти не видно.'),
('Какие месяцы ты знаешь?', 1, 2, 'Январь, февраль, март, апрель, май, июнь, июль, август, сентябрь, октябрь, ноябрь, декабрь.', 'Названия месяцев пришли к нам из Древнего Рима! Например, «июль» назван в честь Юлия Цезаря, а «август» — в честь императора Августа. А на Руси месяцы называли иначе: январь был «просинец», а август — «серпень».', 'Давай начнём с самого начала года'),
('Первый весенний цветок', 1, 2, 'Подснежник', 'Подснежник занесён в Красную книгу! Его нельзя срывать, иначе луковица в земле погибнет и не даст новых цветов на следующий год. Эти цветы очень хрупкие, и их нужно беречь.', 'Вспомни, какие цветы появляются в лесу сразу после схода снега.'),
('Как называют танцора на льду', 1, 2, 'Фигурист', 'Слово «фигурное катание» произошло от обязательных когда-то «фигур» — кругов, восьмёрок и других элементов, которые катающиеся должны были вычерчивать на льду', 'Этот спортсмен скользит по льду на коньках и выполняет красивые движения, как танцор, только на ледяной площадке.'),
('Птица, которую называют лесным доктором', 1, 2, 'Дятел', 'Дятел — самый главный санитар леса! Его длинный язык помогает ему доставать из-под коры насекомых, которые могут сделать дерево больным.', 'Эта птица целый день стучит клювом по деревьям и лечит их от вредных жучков.'),
('С какого месяца начинается зима', 1, 2, 'Декабрь', 'Декабрь — последний месяц года. В декабре отмечаются множество праздников, включая Рождество, Хануку, Кванзу и Новый год.', 'Вспомни, в каком месяце мы начинаем готовиться к Новому году.'),
('Сколько цветов на флаге России', 1, 2, 'Три цвета', 'Трёхцветный флаг ввёл ещё Пётр I для торговых судов. А день Государственного флага России мы отмечаем 22 августа!', 'Вспомни, какие цвета на флаге и как они расположены'),
('Самый маленький палец на руке', 1, 2, 'Мизинец', 'Слово «мизинец» происходит от древнерусского слова «мизинок» или «мизиньць». Так обычно называли самого младшего сына в семье.', 'Покажи все пальчики на своей руке: большой, указательный, средний, безымянный... и какой остался?'),
('Какие ты знаешь цветы?', 1, 2, 'Роза, ромашка, тюльпан, одуванчик, колокольчик.', 'Некоторые цветы занесены в Красную книгу, например, ландыш и подснежник. Их нельзя срывать, иначе они могут исчезнуть! А подсолнух поворачивает свою головку вслед за солнцем.', 'Вспомни, какие красивые растения с лепестками растут на клумбе в парке или продаются в цветочном магазине?');
